import React, { useState } from 'react';

export default function EmailReplyGenerator() {
  const [emailContent, setEmailContent] = useState('');
  const [tone, setTone] = useState('professional');
  const [reply, setReply] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [copied, setCopied] = useState(false);

  const generateReply = async () => {
    if (!emailContent.trim()) {
      setError('Please enter an email to reply to');
      return;
    }

    setLoading(true);
    setError('');
    setReply('');

    try {
      const response = await fetch('http://localhost:3001/generate-reply', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          email: emailContent,
          tone: tone
        }),
      });

      if (!response.ok) {
        throw new Error('Failed to generate reply');
      }

      const data = await response.json();
      setReply(data.reply);
    } catch (err) {
      setError('Failed to generate reply. Please try again.');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const copyToClipboard = () => {
    navigator.clipboard.writeText(reply);
    setCopied(true);
    setTimeout(() => setCopied(false), 2000);
  };

  return (
    <div style={{ minHeight: '100vh', background: 'linear-gradient(to bottom right, #eff6ff, #e0e7ff)', padding: '2rem' }}>
      <div style={{ maxWidth: '800px', margin: '0 auto' }}>
        <div style={{ textAlign: 'center', marginBottom: '2rem' }}>
          <h1 style={{ fontSize: '2rem', fontWeight: 'bold', color: '#1f2937', marginBottom: '0.5rem' }}>
            üìß Email Reply Generator
          </h1>
          <p style={{ color: '#6b7280' }}>Generate professional email replies instantly with AI</p>
        </div>

        <div style={{ background: 'white', borderRadius: '8px', boxShadow: '0 4px 6px rgba(0,0,0,0.1)', padding: '1.5rem', marginBottom: '1.5rem' }}>
          <label style={{ display: 'block', fontSize: '0.875rem', fontWeight: '500', color: '#374151', marginBottom: '0.5rem' }}>
            Original Email
          </label>
          <textarea
            value={emailContent}
            onChange={(e) => setEmailContent(e.target.value)}
            placeholder="Paste the email you want to reply to..."
            style={{ 
              width: '100%', 
              height: '160px', 
              padding: '0.75rem', 
              border: '1px solid #d1d5db', 
              borderRadius: '8px',
              fontSize: '1rem',
              resize: 'none'
            }}
          />

          <div style={{ marginTop: '1rem' }}>
            <label style={{ display: 'block', fontSize: '0.875rem', fontWeight: '500', color: '#374151', marginBottom: '0.5rem' }}>
              Reply Tone
            </label>
            <div style={{ display: 'flex', gap: '0.5rem', flexWrap: 'wrap' }}>
              {['professional', 'friendly', 'casual', 'formal'].map((t) => (
                <button
                  key={t}
                  onClick={() => setTone(t)}
                  style={{
                    padding: '0.5rem 1rem',
                    borderRadius: '8px',
                    fontWeight: '500',
                    border: 'none',
                    cursor: 'pointer',
                    background: tone === t ? '#4f46e5' : '#f3f4f6',
                    color: tone === t ? 'white' : '#374151'
                  }}
                >
                  {t.charAt(0).toUpperCase() + t.slice(1)}
                </button>
              ))}
            </div>
          </div>

          <button
            onClick={generateReply}
            disabled={loading}
            style={{
              marginTop: '1.5rem',
              width: '100%',
              background: loading ? '#9ca3af' : '#4f46e5',
              color: 'white',
              padding: '0.75rem',
              borderRadius: '8px',
              fontWeight: '500',
              border: 'none',
              cursor: loading ? 'not-allowed' : 'pointer',
              fontSize: '1rem'
            }}
          >
            {loading ? '‚è≥ Generating...' : '‚úâÔ∏è Generate Reply'}
          </button>

          {error && (
            <div style={{ 
              marginTop: '1rem', 
              padding: '0.75rem', 
              background: '#fef2f2', 
              border: '1px solid #fecaca', 
              borderRadius: '8px', 
              color: '#b91c1c' 
            }}>
              {error}
            </div>
          )}
        </div>

        {reply && (
          <div style={{ background: 'white', borderRadius: '8px', boxShadow: '0 4px 6px rgba(0,0,0,0.1)', padding: '1.5rem' }}>
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '1rem' }}>
              <h2 style={{ fontSize: '1.125rem', fontWeight: '600', color: '#1f2937' }}>Generated Reply</h2>
              <button
                onClick={copyToClipboard}
                style={{
                  display: 'flex',
                  alignItems: 'center',
                  gap: '0.5rem',
                  padding: '0.375rem 0.75rem',
                  background: '#f3f4f6',
                  border: 'none',
                  borderRadius: '8px',
                  cursor: 'pointer',
                  fontSize: '0.875rem'
                }}
              >
                {copied ? '‚úÖ Copied!' : 'üìã Copy'}
              </button>
            </div>
            <div style={{ 
              padding: '1rem', 
              background: '#f9fafb', 
              borderRadius: '8px', 
              whiteSpace: 'pre-wrap', 
              color: '#374151',
              lineHeight: '1.6'
            }}>
              {reply}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
